import React, { useEffect, useState } from 'react';
import './App.css';
import {
  FormControl,
  Select,
  Card,
  CardContent,
  MenuItem
} from "@mui/material";
import InfoBox from './components/InfoBox';
import Map from './components/Map';
import TableS from './components/Table';
import LineGraph from './components/LineGraph';
import { prettyPrintStat, sortData } from './util';
import "leaflet/dist/leaflet.css";
import numeral from "numeral";

function App() {
  // // All state means how to write variables in react
  // const [countryInfo, setCountryInfo] = useState<any[]>([]);
  // const [countries, setCountries] =useState<any[]>([]);
  // const [country, setInputCountry] =useState(["worldwide"]);
  // const [mapCenter, setMapCenter] = useState([34.80746, -40.4796 ]);
  // // useState([34.80746, -40.4796]);
  // const [mapZoom, setMapZoom] = useState(3);
  // const [casesType, setCasesType] = useState("cases");
  // const [mapCountries, setMapCountries] = useState([]);
  // const [tableData, setTableData] = useState<any[]>([]);

  const [country, setInputCountry] = useState("worldwide");
  const [countryInfoMa, setCountryInfo] = useState({});
  const [countries, setCountries] = useState<any[]>([]);
  const [mapCountries, setMapCountries] = useState<any[]>([]);
  const [tableData, setTableData] =  useState<any[]>([]);
  const [casesType, setCasesType] = useState("cases");
  const [mapCenter, setMapCenter] = useState([34.80746, -40.4796 ]);
  const [mapZoom, setMapZoom] = useState(3);
  // const [todayCase, setTodaysCase] = useState<any[]>([]);
  // const [totalC, setTotalCase] = useState<any[]>([]);

  // useEffect(() => {
  //   fetch("https://disease.sh/v3/covid-19/all")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setCountryInfo(data);
  //       // const totalCurrentCases =  data.cases;
  //       // const totalCurrentRecovered =  data.recovered;
  //       // const totalCurrentDeaths =  data.deaths;
  //       // const todaysCurrentCases =  data.todayCases;
  //       // const todaysCurrentRecovered =  data.todayRecovered;
  //       // const todaysCurrentDeaths =  data.todayDeaths;
  //       // console.log(todaysCurrentCases);
  //       // console.log(todaysCurrentRecovered);
  //       // console.log(todaysCurrentDeaths);
  //       // console.log(totalCurrentCases);
  //       // console.log(totalCurrentRecovered);
  //       // console.log(totalCurrentDeaths);

  //     });
  // }, []);

  // useEffect(() => {
  //   fetch("https://disease.sh/v3/covid-19/all")
  //     .then((response) => response.json())
  //     .then((items) => {
  //       console.log(items);
  //       setCountryInfo(items);
  //       setTodaysCase(items.todayCases);
  //       setTotalCase(items.cases);
  //       console.log(items.todayCases);
  //       console.log(items.cases);
  //       // const totalCurrentCases =  data.cases;
  //       // const totalCurrentRecovered =  data.recovered;
  //       // const totalCurrentDeaths =  data.deaths;
  //       // const todaysCurrentCases =  data.todayCases;
  //       // const todaysCurrentRecovered =  data.todayRecovered;
  //       // const todaysCurrentDeaths =  data.todayDeaths;
  //       // console.log(todaysCurrentCases);
  //       // console.log(todaysCurrentRecovered);
  //       // console.log(todaysCurrentDeaths);
  //       // console.log(totalCurrentCases);
  //       // console.log(totalCurrentRecovered);
  //       // console.log(totalCurrentDeaths);

  //     });
  // }, []);

  // useEffect(() => {
  //   getCountries();
  // }, []);

  // const getCountries = async () => {
  //   const response = await fetch("https://disease.sh/v3/covid-19/countries");
  //   const data = await response.json();
  //   let sortedData = sortData(data);
  //   setCountries(data);
  //   setTableData(sortedData);
  //   setMapCountries(data);
  //   // console.log(data);
  // }

  useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/all")
      .then((response) => response.json())
      .then((data) => {
        setCountryInfo(data);
      });
  }, []);

  useEffect(() => {
    const getCountries = async () => {
        const response = await fetch("https://disease.sh/v3/covid-19/countries");
        const data = await response.json();
        let sortedData = sortData(data);
        setCountries(data);
        setTableData(sortedData);
        setMapCountries(data);
        console.log(data);
      };
      getCountries();
  }, []);


  const onCountryChange = async (e: { target: { value: any; }; }) => {
    const countryCode = e.target.value;

    const url =
      countryCode === "worldwide"
        ? "https://disease.sh/v3/covid-19/all"
        : `https://disease.sh/v3/covid-19/countries/${countryCode}`;
    await fetch(url)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setInputCountry(countryCode);
        setCountryInfo(data);
        setMapCenter([data.countryInfo.lat, data.countryInfo.long]);
        setMapZoom(4);
      });
  };

// console.log(countryInfoMa.cases);
// console.log(countryInfoMa.todayCases);
// console.log({totalC});

  return (
    <div className="App">
      <div className="app__left">
        <div className="app__header">
        <h1>COVID 19 TRACKER</h1>
        <p>{countryInfoMa.cases}</p>
        <p>{countryInfoMa.todayCases}</p>
         <FormControl className="app__dropdown">
          <Select variant="outlined" 
          value={country}
          onChange={onCountryChange}
          >
       {/* Loop through all the countries and dropdown */}
       <MenuItem value="worldwide">Worldwide</MenuItem>

       {countries.map((country, i) => (
          <MenuItem key={i} value={country.countryInfo.iso2}>{country.country}</MenuItem>
        ))}
        </Select>
    </FormControl>
    </div>

        <div className='app__stats'>
        <InfoBox
            onClick={(e: any) => setCasesType("cases")}
            title="Coronavirus Cases"
            isRed
            active={casesType === "cases"}
            // cases="123"
            // total="12345"
            // cases={prettyPrintStat(countryInfoMa)}
            // total={numeral(countryInfoMa).format("0.0a")}
            cases={prettyPrintStat(countryInfoMa.todayCases)}
            total={numeral(countryInfoMa.cases).format("0.0a")}
          />
          <InfoBox
            onClick={(e: any) => setCasesType("recovered")}
            title="Coronavirus Recovered"
            isRed
            active={casesType === "recovered"}
            cases="123"
            total="12345"
            // cases={todayCase}
            // total={numeral({totalC}).format("0.0a")}
            // cases={prettyPrintStat(countryInfo.todayRecovered)}
            // total={numeral(countryInfo.recovered).format("0.0a")}
          />
          <InfoBox
            onClick={(e: any) => setCasesType("deaths")}
            title="Coronavirus Deaths"
            isRed
            active={casesType === "deaths"}
            cases="123"
            total="12345"
            // cases={prettyPrintStat({todayCase})}
            // total={numeral({totalC}).format("0.0a")}
            // cases={prettyPrintStat(countryInfo.todayDeaths)}
            // total={numeral(countryInfo.deaths).format("0.0a")}
            // cases={prettyPrintStat(countryInfo.todayDeaths)}
            // total={numeral(countryInfo.deaths).format("0.0a")}
          />
        </div>
        {/* <p onClick={(e: any) => setCasesType("cases")}>{countryInfo.todayCases}</p>
        <p onClick={(e: any) => setCasesType("cases")}>{numeral({countryInfo.cases}).format("0.0a")}</p> */}
        <Map
          countries={mapCountries}
          casesType={casesType}
          center={mapCenter}
          zoom={mapZoom}
         />
      </div>

    <Card className="app__right">
        <CardContent>
          <div className="app__information">
            <h3>Live Cases by Country</h3>
            <TableS countries={tableData} />
            <h3 className='app__graphTittle'>World Wild New {casesType}</h3>
            <LineGraph className="app__graph" casesType={casesType} />
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default App;
