import React, { useEffect, useState } from 'react';
import './App.css';
import {
  FormControl,
  Select,
  MenuItem
} from "@mui/material";

function App() {
  // All state means how to write variables in react
  const [countryInfo, setCountryInfo] = useState({});
  const [countries, setCountries] =useState<any[]>([]);
  const [country, setInputCountry] =useState(["worldwide"]);
  const [mapCenter, setMapCenter] = useState({ lat: 34.80746, lng: -40.4796 });
  const [mapZoom, setMapZoom] = useState(3);

  useEffect(() => {
    getCountries();
  }, []);

  const getCountries = async () => {
    const response = await fetch("https://disease.sh/v3/covid-19/countries");
    const data = await response.json();
    const countries = data.map((country: { country: any; countryInfo: { iso2: any; }; }) => ({
      name: country.country,
      value: country.countryInfo.iso2,
    }));
    setCountries(data);
    // console.log(data);
  }

  const onCountryChange = async (e: { target: { value: any; }; }) => {
    const countryCode = e.target.value;

    const url =
      countryCode === "worldwide"
        ? "https://disease.sh/v3/covid-19/all"
        : `https://disease.sh/v3/covid-19/countries/${countryCode}`;
    await fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setInputCountry(countryCode);
        setCountryInfo(data);
        console.log(data);
        // setMapCenter([data.countryInfo.lat, data.countryInfo.long]);
        setMapZoom(4);
      });
  };
  return (
    <div className="App">
      <h1>COVID 19 TRACKER</h1>

      <FormControl className="app__dropdown">
            <Select variant="outlined" 
            value={country}
            onChange={onCountryChange}
            >
             {/* Loop through all the countries and dropdown */}
             <MenuItem value="worldwide">Worldwide</MenuItem>

              {countries.map((country: { countryInfo: { id: string | number | readonly string[] | undefined; }; country: string | number | boolean | React.ReactElement<any, string | React.JSXElementConstructor<any>> | React.ReactFragment | React.ReactPortal | null | undefined; }) =>(
                  <MenuItem value={country.countryInfo.id}>{country.country}</MenuItem>
                ))}

              {/* <MenuItem value="worldwide">Worldwide 1</MenuItem>
              <MenuItem value="worldwide">Worldwide 2</MenuItem>
              <MenuItem value="worldwide">Worldwide 3</MenuItem>
              <MenuItem value="worldwide">Worldwide 4</MenuItem> */}
            </Select>
          </FormControl>

      {/* Header */}

      {/* Title + Dropdown */}

      {/* InfoBoxes */}

      {/* Tables */}

      {/* Map */}

    </div>
  );
}

export default App;
